    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    if(jCheckBox1.isSelected())
        jTextArea1.setText(jTextArea1.getText().replaceAll(jTextField1.getText(), jTextField2.getText()));
    else
        jTextArea1.setText(jTextArea1.getText().replaceFirst(jTextField1.getText(), jTextField2.getText()));    // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

